export default function Sidebar() {
    const { user } = useAuth()
    const pathname = usePathname();
    const { isOpen, setOpen } = useGlobalContextProvider()

    const toggleSidebar = () => {
        setOpen(!isOpen)
    }
    const closeSidebar = () => {
        setOpen(false)
    }

    const [openItems, setOpenItems] = useState({});

    const handleToggle = (index) => {
        setOpenItems((prevOpenItems) => ({
            ...prevOpenItems,
            [index]: !prevOpenItems[index],
        }));
    };

    // Helper function to determine if any child item is active
    const isAnyChildActive = (item) => {
        if (item.link && pathname === item.link) {
            return true;
        }
        if (item.children && item.children.length > 0) {
            return item.children.some(isAnyChildActive);
        }
        return false;
    };

    const handleItemClick = (link, hasChildren) => {
        // If the item has children, do not close the sidebar
        if (!hasChildren) {
            closeSidebar();
        }
    };

    const items = SidenavData;

    const renderItems = (items, parentIndex = "") => {
        return (
            <div className={` ${isOpen ? "block" : "hidden"}`}>
                {items.map((item, index) => {

                    const itemIndex = parentIndex ? `${parentIndex}-${index}` : `${index}`;
                    const isOpenItem = openItems[itemIndex];
                    const hasChildren = item.children && item.children.length > 0;

                    if (item.title === "Leadership Bonus" && user?.leadership === "0") {
                        return null;
                    }

                    const isActive = pathname === item.link || isAnyChildActive(item);
                    return (
                        <div key={itemIndex} className="my-1">
                            {hasChildren ? (
                                <div className={cn('cursor-pointer items-center justify-between  hover:bg-button-hover-color py-3 px-4 rounded-md ', isOpen ? "flex" : "hidden ", isActive ? "bg-[#f8911e] hover:bg-[#f8911e]" : "")} onClick={() => handleToggle(itemIndex)}>
                                    <div className={cn(isOpen ? 'flex gap-2' : "hidden")}>
                                        { item.icon }
                                        { item.title }
                                    </div>
                                    <div className={cn(isOpenItem ? "rotate-180" : "rotate-0", "transition-all duration-200 ease-in-out ")}>
                                        {<svg
                                            viewBox="0 0 1024 1024"
                                            fill="currentColor"
                                            className='size-5'
                                        >
                                            <path d="M884 256h-75c-5.1 0-9.9 2.5-12.9 6.6L512 654.2 227.9 262.6c-3-4.1-7.8-6.6-12.9-6.6h-75c-6.5 0-10.3 7.4-6.5 12.7l352.6 486.1c12.8 17.6 39 17.6 51.7 0l352.6-486.1c3.9-5.3.1-12.7-6.4-12.7z" />
                                        </svg>}
                                    </div>
                                </div>
                            ) : (item.title === "Logout" ? <div className={cn('w-full', isOpen ? "flex" : "hidden")}>
                                <div className='w-full '>
                                    <Logout className="rounded-md hover:bg-button-hover-color py-3 px-4" title="Log Out" />
                                </div>
                            </div> :
                                <Link href={item.link} className={cn(' gap-2 items-center hover:bg-button-hover-color py-3 px-4 rounded-md', isOpen ? "flex" : "hidden", isActive ? "bg-[#f8911e] hover:bg-[#f8911e]" : "")}
                                    onClick={() => handleItemClick(item.link, hasChildren)}
                                >
                                    {item.icon}
                                    <label className='cursor-pointer'> {item.title} </label>
                                </Link>
                            )}
                            {isOpenItem && hasChildren &&
                                (<div className='md:pl-5 rounded-md'>
                                    {renderItems(item.children, itemIndex)}
                                </div>)
                            }
                        </div>
                    )
                })}
            </div>
        )
    }
    return (
        <div className={cn(isOpen ? " min-w-56 lg:min-w-60" : " w-0", " bg-glass-color glassBlur flex flex-col pt-5 pb-5 max-lg:absolute max-lg:top-0 max-lg:left-0 max-lg:z-20 overflow-hidden h-screen relative transition-all duration-200 ease-in-out ")}>
            <div className='px-3 py-5 flex gap-3 items-center justify-start'>
                <svg
                    viewBox="0 0 512 512"
                    fill="currentColor"
                    className=" size-16 bg-blue-500 rounded-full p-1 shadow-shadow-bg"
                >
                    <path d="M256,508C117.04,508,4,394.96,4,256S117.04,4,256,4s252,113.04,252,252S394.96,508,256,508z" fill="#FFFFFF" />
                    <path d="M256,8c136.752,0,248,111.248,248,248S392.752,504,256,504S8,392.752,8,256S119.248,8,256,8 M256,0 C114.608,0,0,114.608,0,256s114.608,256,256,256s256-114.608,256-256S397.392,0,256,0L256,0z" fill="#D6D6D6" />
                    <ellipse cx="256" cy="175.648" rx="61.712" ry="60.48" fill="#0BA4E0" />
                    <path d="M362.592,360.624c0-57.696-47.728-104.464-106.592-104.464s-106.592,46.768-106.592,104.464H362.592z" fill="#0BA4E0" />
                </svg>
                <div className='flex flex-col gap-1'>
                    <h2 className='text-2xl font-semibold'>
                        {user?.username || "username"}
                    </h2>
                </div>
            </div>
            <div className="no-scrollbar h-full overflow-y-scroll " >
                {renderItems(items)}
            </div>
            <div className="cursor-pointer absolute top-1 right-1 z-40 " onClick={toggleSidebar}>
                <svg
                    viewBox="0 0 1024 1024"
                    fill="currentColor"
                    className='size-10'
                >
                    <path d="M512 64C264.6 64 64 264.6 64 512s200.6 448 448 448 448-200.6 448-448S759.4 64 512 64zm165.4 618.2l-66-.3L512 563.4l-99.3 118.4-66.1.3c-4.4 0-8-3.5-8-8 0-1.9.7-3.7 1.9-5.2l130.1-155L340.5 359a8.32 8.32 0 01-1.9-5.2c0-4.4 3.6-8 8-8l66.1.3L512 464.6l99.3-118.4 66-.3c4.4 0 8 3.5 8 8 0 1.9-.7 3.7-1.9 5.2L553.5 514l130 155c1.2 1.5 1.9 3.3 1.9 5.2 0 4.4-3.6 8-8 8z" />
                </svg>
            </div>
        </div>
    )
}

